import re
import collections

class Json_to_sentence():
    """Convert the plain object description of the mdp into gamma and T and R matrices"""
    def __init__(self, offerjson):
        '''
    Initiate the object
        '''
        self.offerjson = offerjson
        self.url = offerjson["url"]
        self.timestamp = offerjson["result"]["timestamp"]
        self.content = [i.strip() for i in offerjson["result"]["extractorData"]["data"][0]["group"][0]["overall"][0]["text"].split("\n") if i != " "]
        self.first_category = []
        self.first_letters_cap = []
        self.policy = []
        self.date = []
        self.sentences = collections.defaultdict(list)
    
    def parse_articles(self, iterable):
        '''
        Find the first layer of keysst level of keys
        '''
        for i, section in enumerate(iterable):
            self.first_category.append((re.findall('([A-Z]+(?=\s+[A-Z])(?:\s[A-Z][A-Z]+)+)', section), i)) 
            ## this is to have the first letter as Capital letter
            self.first_letters_cap.append((re.findall('([A-Z][a-z]+(?=\s+[A-Z])(?:\s[A-Z][a-z]+)+)', section), i))
            ## this is to pick out all capital letters       
            word_list = [w.strip(',|.') for w in section.split()]
            ## this is to pick out all capital letters       
            for ele in word_list:
                if re.search('^[A-Z]+[0-9]{3,}$', ele):
                    self.policy.append((ele, i))
                if re.search('^(((0?[1-9]|1[012])/(0?[1-9]|1\d|2[0-8])|(0?[13456789]|1[012])/(29|30)|(0?[13578]|1[02])/31)  \
                /(19|[2-9]\d)\d{2}|0?2/29/((19|[2-9]\d)(0[48]|[2468][048]|[13579][26])|(([2468][048]|[3579][26])00)))$', ele):
                    self.date.append((ele, i))
    
#        return self.first_category, self.first_letters_cap, self.policy, self.date
    
